import { initMixin } from './init'
import { stateMixin } from './state'
import { renderMixin } from './render'
import { eventsMixin } from './events'
import { lifecycleMixin } from './lifecycle'
import { warn } from '../util/index'
// Vue本身就是一个函数 ,使用函数实现的一个类,类的原型上上挂载了很多方法
function Vue(options) {
    if (process.env.NODE_ENV !== 'production' &&
        !(this instanceof Vue)
    ) {
        // ES5实现Class的一种方式,通过函数
        warn('Vue是一个构造函数，应使用`new`关键字进行调用');
    }
    // 调用_init方法
    this._init(options)
}
// 每个Mixin就是往原型上挂载一些定义的方法
// 拆分成单文件是为了方便Vue进行管理
initMixin(Vue)
stateMixin(Vue)
eventsMixin(Vue)
lifecycleMixin(Vue)
renderMixin(Vue)

export default Vue