/* @flow */

import { warn } from 'core/util/index'

export * from './attrs'
export * from './class'
export * from './element'

/**
 * 查询一个元素选择器，如果它还不是一个元素。
 */
export function query(el: string | Element): Element {
    if (typeof el === 'string') {
        // 如果这是个字符串比如：#app,就将这个el通过document.querySelector 
        // DOM对象获取过来，这是快递车的终点站
        const selected = document.querySelector(el)
            // 判断这个el在没在页面中 ，如果是凭空产生的就发起警告说：不能找到这个具有el的div或者其他元素
        if (!selected) {
            process.env.NODE_ENV !== 'production' && warn(
                    'Cannot find element: ' + el
                )
                // 然后Vue自动的帮忙创建这个div
                // debugger
            return document.createElement('div')
        }
        return selected
    } else {
        return el
    }
}